    def __init__(self):
        self.fibonacci_cache = {}

    def get_fibonacci_number(self, n):
        if n < 0:
            print("Input cannot be negative")
            return None

        if n <= 1:
            return n

        # check if it is in cache
        if n in self.fibonacci_cache:
            return self.fibonacci_cache[n]

        # calculate it and add it to cache 
        self.fibonacci_cache[n] = self.get_fibonacci_number(n - 1) + self.get_fibonacci_number(n - 2)
        return self.fibonacci_cache[n]

