```c
#include <SDL2/SDL.h>
#include <stdio.h>

#define SCREEN_WIDTH 800
#define SCREEN_HEIGHT 600
#define SNAKE_SIZE 20

int main(int argc, char* argv[]) {
    // Initialize SDL2
    if (SDL_Init(SDL_INIT_VIDEO) < 0) {
        printf("SDL could not initialize! SDL_Error: %s\n", SDL_GetError());
        return 1;
    }

    // Create window
    SDL_Window* window = SDL_CreateWindow("Snake Game", SDL_WINDOWPOS_UNDEFINED, SDL_WINDOWPOS_UNDEFINED, SCREEN_WIDTH, SCREEN_HEIGHT, SDL_WINDOW_SHOWN);
    if (window == NULL) {
        printf("Window could not be created! SDL_Error: %s\n", SDL_GetError());
        return 1;
    }

    // Create renderer
    SDL_Renderer* renderer = SDL_CreateRenderer(window, -1, SDL_RENDERER_ACCELERATED);
    if (renderer == NULL) {
        printf("Renderer could not be created! SDL_Error: %s\n", SDL_GetError());
        return 1;
    }

    // Load images
    SDL_Surface* snakeImage = IMG_Load("snake.png");
    SDL_Surface* foodImage = IMG_Load("food.png");
    SDL_Surface* backgroundImage = IMG_Load("background.png");

    // Convert images to textures
    SDL_Texture* snakeTexture = SDL_CreateTextureFromSurface(renderer, snakeImage);
    SDL_Texture* foodTexture = SDL_CreateTextureFromSurface(renderer, foodImage);
    SDL_Texture* backgroundTexture = SDL_CreateTextureFromSurface(renderer, backgroundImage);

    // Initialize game state
    int snakeX = SCREEN_WIDTH / 2;
    int snakeY = SCREEN_HEIGHT / 2;
    int snakeDirection = 0; // 0: up, 1: right, 2: down, 3: left
    int score = 0;

    // Game loop
    bool running = true;
    while (running) {
        // Handle events
        SDL_Event event;
        while (SDL_PollEvent(&event)) {
            if (event.type == SDL_QUIT) {
                running = false;
            } else if (event.type == SDL_KEYDOWN) {
                switch (event.key.keysym.sym) {
                    case SDLK_UP:
                        snakeDirection = 0;
                        break;
                    case SDLK_RIGHT:
                        snakeDirection = 1;
                        break;
                    case SDLK_DOWN:
                        snakeDirection = 2;
                        break;
                    case SDLK_LEFT:
                        snakeDirection = 3;
                        break;
                }
            }
        }

        // Update game state
        switch (snakeDirection) {
            case 0:
                snakeY -= SNAKE_SIZE;
                break;
            case 1:
                snakeX += SNAKE_SIZE;
                break;
            case 2:
                snakeY += SNAKE_SIZE;
                break;
            case 3:
                snakeX -= SNAKE_SIZE;
                break;
        }

        // Check for collisions
        if (snakeX < 0 || snakeX >= SCREEN_WIDTH || snakeY < 0 || snakeY >= SCREEN_HEIGHT) {
            running = false;
        }

        // Render
        SDL_RenderClear(renderer);
        SDL_RenderCopy(renderer, backgroundTexture, NULL, NULL);
        SDL_RenderCopy(renderer, snakeTexture, NULL, &SDL_Rect{snakeX, snakeY, SNAKE_SIZE, SNAKE_SIZE});
        SDL_RenderCopy(renderer, foodTexture, NULL, &SDL_Rect{SCREEN_WIDTH / 2, SCREEN_HEIGHT / 2, SNAKE_SIZE, SNAKE_SIZE});
        SDL_RenderPresent(renderer);

        // Check for food collision
        if (snakeX == SCREEN_WIDTH / 2 && snakeY == SCREEN_HEIGHT / 2) {
            score++;
            // Generate new food
        }
    }

    // Clean up
    SDL_DestroyTexture(snakeTexture);
    SDL_DestroyTexture(foodTexture);
    SDL_DestroyTexture(backgroundTexture);
    SDL_DestroyRenderer(renderer);
    SDL_DestroyWindow(window);
    SDL_Quit();

    return 0;
}
```

