    std::string filename;
    std::cout << "Enter the name of the file: ";
    std::cin >> filename;

    std::string word;
    std::cout << "Enter the word to search for: ";
    std::cin >> word;

    int count = 0;
    std::ifstream file(filename);
    if (file.is_open()) {
        std::string line;
        while (std::getline(file, line)) {
            std::istringstream iss(line);
            std::string token;
            while (iss >> token) {
                if (token == word) {
                    count++;
                }
            }
        }
        file.close();
    } else {
        std::cerr << "Unable to open file." << std::endl;
        return 1;
    }

    std::cout << "The word '" << word << "' appears " << count << " times in the file." << std::endl;

    return 0;
}
```

