#include <iostream>
#include <string>
#include <cassert>

std::string markdown_to_html(const std::string& markdown) {
    std::string html;
    size_t pos = 0;
    while (pos < markdown.size()) {
        if (markdown.substr(pos, 2) == "**") {
            size_t end = markdown.find("**", pos + 2);
            if (end != std::string::npos) {
                html += "<strong>" + markdown.substr(pos + 2, end - pos - 2) + "</strong>";
                pos = end + 2;
            } else {
                break; // Unterminated bold
            }
        } else if (markdown.substr(pos, 1) == "*") {
            size_t end = markdown.find("*", pos + 1);
            if (end != std::string::npos) {
                html += "<em>" + markdown.substr(pos + 1, end - pos - 1) + "</em>";
                pos = end + 1;
            } else {
                break; // Unterminated italic
            }
        } else if (markdown.substr(pos, 3) == "~~~") {
            size_t end = markdown.find("~~~", pos + 3);
            if (end != std::string::npos) {
                html += "<del>" + markdown.substr(pos + 3, end - pos - 3) + "</del>";
                pos = end + 3;
            } else {
                break; // Unterminated strikethrough
            }
        } else if (markdown.substr(pos, 1) == "`") {
            size_t end = markdown.find("`", pos + 1);
            if (end != std::string::npos) {
                html += "<code>" + markdown.substr(pos + 1, end - pos - 1) + "</code>";
                pos = end + 1;
            } else {
                break; // Unterminated inline code
            }
        } else if (markdown.substr(pos, 1) == ">") {
            size_t end = markdown.find("\n", pos + 1);
            if (end != std::string::npos) {
                html += "<blockquote>" + markdown.substr(pos + 1, end - pos - 1) + "</blockquote>";
                pos = end + 1;
            } else {
                break; // Unterminated blockquote
            }
        } else {
            size_t nextPos = markdown.find_first_of("**_*~~`>", pos);
            if (nextPos == std::string::npos) {
                nextPos = markdown.size();
            }
            html += markdown.substr(pos, nextPos - pos);
            pos = nextPos;
        }
    }
    return html;
}

void check_markdown_to_html() {
    assert(markdown_to_html("**bold**") == "<strong>bold</strong>");
    assert(markdown_to_html("*italic*") == "<em>italic</em>");
    assert(markdown_to_html("~~struck~~") == "<del>struck</del>");
    assert(markdown_to_html("`inline`") == "<code>inline</code>");
    assert(markdown_to_html("> blockquote") == "<blockquote>blockquote</blockquote>");
    assert(markdown_to_html("**bold *and* italic**") == "<strong>bold <em>and</em> italic</strong>");
    assert(markdown_to_html("*italic **and** bold*") == "<em>italic <strong>and</strong> bold</em>");
    assert(markdown_to_html("~~strikethrough `code`~~") == "<del>strikethrough <code>code</code></del>");
    assert(markdown_to_html("> blockquote **with** *bold* and `code`") == "<blockquote><strong>with</strong> <em>bold</em> and <code>code</code></blockquote>");
}

int main() {
    check_markdown_to_html();
    return 0;
}
```

