    name: 'ban',
    description: 'Bans a user',
    options: [
      {
        name: 'user',
        description: 'The user to ban',
        required: true,
        type: 6 // User
      },
      {
        name: 'reason',
        description: 'The reason for the ban',
        required: true,
        type: 3 // String
      }
