```cpp
#include <iostream>
#include <string>
#include <map>
#include <cctype>

std::string convertMarkdownToHTML(const std::string& markdownText) {
    std::map<char, std::string> markdownToHTML = {
        {'*', "<em>", "</em>"},
        {'**', "<strong>", "</strong>"},
        {'[', "[", "]("},
        {']', "", ")"}
    };

    std::string htmlText;
    int depth = 0;
    char prevChar = '\0';

    for (char c : markdownText) {
        if (c == '[') {
            if (prevChar == '[' && depth > 0) {
                htmlText += ']';
            }
            depth++;
        } else if (c == ']') {
            if (depth > 0) {
                htmlText += ']';
                depth--;
            }
        } else if (c == '(') {
            if (depth > 0) {
                htmlText += '(';
            }
        } else if (c == ')') {
            if (depth > 0) {
                htmlText += ')';
                depth--;
            }
        } else if (c == '_' || c == '*') {
            htmlText += markdownToHTML[c];
        } else if (depth > 0) {
            htmlText += c;
        }

        prevChar = c;
    }

    return htmlText;
}

int main() {
    std::string markdownExample = "**Bold Text** _Italic Text_ [Visit Example Site](https://example.com)";
    std::string htmlExample = convertMarkdownToHTML(markdownExample);
    std::cout << htmlExample << std::endl;

    return 0;
}
```

