            case KEY_DOWN:
                direction = 1;
                break;
            case KEY_LEFT:
                direction = 2;
                break;
            case KEY_RIGHT:
                direction = 3;
                break;
        }
        if (direction == 0 && snake_y[0] > 0 && snake_x[0] == snake_x[1]) {
            snake_y[0]--;
        } else if (direction == 1 && snake_y[0] < MAX_Y - 1 && snake_x[0] == snake_x[1]) {
            snake_y[0]++;
        } else if (direction == 2 && snake_x[0] > 0 && snake_y[0] == snake_y[1]) {
            snake_x[0]--;
        } else if (direction == 3 && snake_x[0] < MAX_X - 1 && snake_y[0] == snake_y[1]) {
            snake_x[0]++;
        }
        if (snake_x[0] == food.x && snake_y[0] == food.y) {
            score++;
            food.x = rand() % MAX_X;
            food.y = rand() % MAX_Y;
        } else {
            for (int i = length - 1; i > 0; i--) {
                snake_x[i] = snake_x[i - 1];
                snake_y[i] = snake_y[i - 1];
            }
        }
        length++;
        delay(100);
        if (snake_x[0] == 0 || snake_x[0] == MAX_X - 1 || snake_y[0] == 0 || snake_y[0] == MAX_Y - 1) {
            break;
        }
        for (int i = 1; i < length; i++) {
            if (snake_x[0] == snake_x[i] && snake_y[0] == snake_y[i]) {
                break;
            }
        }
        if (i == length) {
            break;
        }
    }
    endwin();
    return 0;
