    if Input.is_action_released("ui_up"):
        moving_up = false
    if Input.is_action_pressed("ui_down"):
        moving_down = true
    if Input.is_action_released("ui_down"):
        moving_down = false

    if moving_left:
        move_input.x -= 1
    if moving_right:
        move_input.x += 1
    if moving_up:
        move_input.y -= 1
    if moving_down:
        move_input.y += 1

    move_and_slide(move_input * speed * delta)
    velocity.y += gravity * delta
