import heapq

class SegmentTree:
    def __init__(self, available_times):
        self.n = len(available_times)
        self.tree = [[0, 0] for _ in range(4 * self.n)]
        self.build(0, 0, self.n - 1, available_times)

    def build(self, node, start, end, available_times):
        if start == end:
            self.tree[node] = [available_times[start][0], available_times[start][1]]
        else:
            mid = (start + end) // 2
            self.build(2 * node + 1, start, mid, available_times)
            self.build(2 * node + 2, mid + 1, end, available_times)
            self.tree[node] = [max(self.tree[2 * node + 1][0], self.tree[2 * node + 2][0]), min(self.tree[2 * node + 1][1], self.tree[2 * node + 2][1])]

