  // SPDX-License-Identifier: MIT
  pragma solidity ^0.8.0;

  import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
  import "@openzeppelin/contracts/access/Ownable.sol";

  contract PresaleContract is Ownable {
      ERC20 public mainToken;
      uint256 public tokensSold;

      constructor(address _tokenAddress) {
          mainToken = ERC20(_tokenAddress);
      }

      function buyTokens(uint256 amount) external payable {
          require(msg.value >= amount * 1 ether, "Insufficient funds");
          require(tokensSold + amount <= 1000000, "Exceeds available tokens");

          tokensSold += amount;
          mainToken.transferFrom(owner(), msg.sender, amount);

          // Refund excess funds
          if (msg.value > amount * 1 ether) {
              payable(msg.sender).transfer(msg.value - amount * 1 ether);
          }
      }

      function finalizePresale() external onlyOwner {
          require(tokensSold > 0, "No tokens sold yet");
          mainToken.transferFrom(owner(), address(this), tokensSold);
      }
  }
  ```

