#include <regex>
#include <string>

std::string markdown_to_html(std::string markdown_text) {
    // Define regular expressions
    std::regex header("^#{1,6} (.*)$", std::regex_constants::multiline);
    std::regex bold("__(.*?)__");
    std::regex italic("*([^*]*)*");
    std::regex link("\\[([^\\[\\]]*)\\]\\((.*?)\\)");
    std::regex image("!\\[([^\\[\\]]*)\\]\\((.*?)\\)");

    // Replace markdown headers with html headers
    markdown_text = std::regex_replace(markdown_text, header, [](const std::smatch& m) {
        int level = std::count(m[0].first, m[0].second, '#');
        return "<h" + std::to_string(level) + ">" + m[1].str + "</h" + std::to_string(level) + ">";
    });

    // Replace markdown bold with html bold
    markdown_text = std::regex_replace(markdown_text, bold, "<b>$1</b>");

    // Replace markdown italic with html italic
    markdown_text = std::regex_replace(markdown_text, italic, "<i>$1</i>");

    // Replace markdown links with html links
    markdown_text = std::regex_replace(markdown_text, link, "<a href=\"$2\">$1</a>");

    // Replace markdown images with html images
    markdown_text = std::regex_replace(markdown_text, image, "<img src=\"$2\" alt=\"$1\">");

    return markdown_text;
}
