        front = 0;
        rear = 0;
        size = 0;
    }

    public void enqueue(int item) {
        if (size == data.length) {
            System.out.println("Queue is full");
            return;
        }
        data[rear] = item;
        rear = (rear + 1) % data.length;
        size++;
    }

