    int x;
    int y;
} Point;

Point snake[100];
int snake_length = 1;
int direction = 0; // 0: up, 1: right, 2: down, 3: left
Point food;

void init_snake() {
    snake[0].x = SCREEN_WIDTH / 2;
    snake[0].y = SCREEN_HEIGHT / 2;
}

void place_food() {
    food.x = rand() % (SCREEN_WIDTH / GRID_SIZE) * GRID_SIZE;
    food.y = rand() % (SCREEN_HEIGHT / GRID_SIZE) * GRID_SIZE;
}

void draw_snake(SDL_Renderer *renderer) {
    SDL_SetRenderDrawColor(renderer, 0, 255, 0, 255);
    for (int i = 0; i < snake_length; i++) {
        SDL_Rect rect = {snake[i].x, snake[i].y, SNAKE_SIZE, SNAKE_SIZE};
        SDL_RenderFillRect(renderer, &rect);
    }
}

void draw_food(SDL_Renderer *renderer) {
    SDL_SetRenderDrawColor(renderer, 255, 0, 0, 255);
    SDL_Rect rect = {food.x, food.y, SNAKE_SIZE, SNAKE_SIZE};
    SDL_RenderFillRect(renderer, &rect);
}

void move_snake() {
    Point new_head = {snake[0].x, snake[0].y};

    switch (direction) {
        case 0: // up
            new_head.y -= GRID_SIZE;
            break;
        case 1: // right
            new_head.x += GRID_SIZE;
            break;
        case 2: // down
            new_head.y += GRID_SIZE;
            break;
        case 3: // left
            new_head.x -= GRID_SIZE;
            break;
    }

    for (int i = snake_length - 1; i > 0; i--) {
        snake[i] = snake[i - 1];
    }
    snake[0] = new_head;

    if (new_head.x == food.x && new_head.y == food.y) {
        snake_length++;
        place_food();
    }
}

int check_collision() {
    for (int i = 1; i < snake_length; i++) {
        if (snake[i].x == snake[0].x && snake[i].y == snake[0].y) {
