SDL_Surface *block;
SDL_Surface *snake[4];
SDL_Surface *food;
SDL_Surface *score_tex;
SDL_Rect score;
SDL_Event event;

int game_over = 0;
int x, y, dx, dy, score_value = 0;

void create_block(void)
