    for (i = 0; i < 4; i++)
    {
        snake[i] = SDL_LoadBMP((char *) ("snake_body.bmp"));
        if (!snake[i])
        {
            printf("Unable to load snake: %s\n", SDL_GetError());
            exit(1);
        }
    }
}

void create_food(void)
{
    food = SDL_LoadBMP("food.bmp");
    if (!food)
    {
        printf("Unable to load food: %s\n", SDL_GetError());
        exit(1);
    }
}

void create_score(void)
{
    TTF_Font *font = TTF_OpenFont("arial.ttf", 24);

    if (!font)
        printf("Unable to load font: %s\n", TTF_GetError());

    score_tex = TTF_RenderText_Blended(font, "Score: 0", SDL_Color{255, 255, 255, 255});
    if (!score_tex)
        printf("Unable to render text: %s\n", TTF_GetError());

    score.x = 10;
    score.y = 10;
    score.w = score_tex->w;
    score.h = score_tex->h;

    TTF_CloseFont(font);
}

void init(void)
{
    SDL_Init(SDL_INIT_VIDEO);
    IMG_Init(IMG_INIT_PNG);
    TTF_Init();

    screen = SDL_SetVideoMode(WIDTH, HEIGHT, 32, SDL_HWSURFACE | SDL_DOUBLEBUF);
    if (!screen)
    {
        printf("Unable to set video mode: %s\n", SDL_GetError());
        exit(1);
    }

    create_block();
    create_snake();
    create_food();
    create_score();

    x = BLOCK_SIZE;
    y = HEIGHT - BLOCK_SIZE * 2;
    dx = BLOCK_SIZE;
    dy = 0;
}

int main(int argc, char **argv)
{
    int i;
    Uint32 last_frame_ticks = 0;

    init();

    while (!game_over)
    {
        SDL_FillRect(screen, NULL, SDL_MapRGB(screen->format, 0, 0, 0));

        SDL_BlitSurface(block, NULL, screen, &score);

        for (i = 0; i < 4; i++)
        {
            SDL_BlitSurface(snake[i], NULL, screen, &snake[i]->clip_rect[i]);
        }

        SDL_BlitSurface(food, NULL, screen, &food->clip_rect);

        if (x == food->clip_rect.x && y == food->clip_rect.y)
        {
            score_value++;
            food->clip_rect.x = rand() % ((WIDTH - food->clip_rect.w) / BLOCK_SIZE) * BLOCK_SIZE;
            food->clip_rect.y = rand() % ((HEIGHT - food->clip_rect.h) / BLOCK_SIZE) * BLOCK_SIZE;

            SDL_FreeSurface(snake[3]);
            snake[3] = SDL_LoadBMP((char *) ("snake_head.bmp"));
        }

        snake[0]->clip_rect.x = x;
        snake[0]->clip_rect.y = y;

        x += dx;
        y += dy;

        if (x < 0 || x > (WIDTH - BLOCK_SIZE) || y < 0 || y > (HEIGHT - BLOCK_SIZE))
            game_over = 1;

        if (x < 0)
            x = 0;
        else if (x > (WIDTH - BLOCK_SIZE))
            x = WIDTH - BLOCK_SIZE;

        if (y < 0)
            y = 0;
        else if (y > (HEIGHT - BLOCK_SIZE))
            y = HEIGHT - BLOCK_SIZE;

        for (i = 1; i < 4; i++)
        {
            if (x == snake[i]->clip_rect.x && y == snake[i]->clip_rect.y)
                game_over = 1;
        }

        SDL_BlitSurface(score_tex, NULL, screen, &score);

        SDL_UpdateRects(screen, 1, NULL);

        while (SDL_PollEvent(&event))
        {
            if (event.type == SDL_QUIT || (event.type == SDL_KEYDOWN && event.key.keysym.scancode == SDL_SCANCODE_ESCAPE))
            {
                game_over = 1;
            }

            if (event.type == SDL_KEYDOWN)
            {
                switch (event.key.keysym.scancode)
                {
                case SDL_SCANCODE_UP:
                    dy = -BLOCK_SIZE;
                    dx = 0;
                    break;
                case SDL_SCANCODE_DOWN:
                    dy = BLOCK_SIZE;
                    dx = 0;
                    break;
                case SDL_SCANCODE_LEFT:
                    dx = -BLOCK_SIZE;
                    dy = 0;
                    break;
                case SDL_SCANCODE_RIGHT:
                    dx = BLOCK_SIZE;
                    dy = 0;
                    break;
                }
            }
        }

        SDL_Delay(SNAKE_SPEED - (SDL_GetTicks() - last_frame_ticks));
        last_frame_ticks = SDL_GetTicks();
    }

    SDL_FreeSurface(block);
    for (int i = 0; i < 4; i++)
        SDL_FreeSurface(snake[i]);
    SDL_FreeSurface(food);
    SDL_FreeSurface(score_tex);
    SDL_Quit();
    IMG_Quit();
    TTF_Quit();

    return 0;
}
