#include <iostream>
#include <string>
#include <vector>
#include <map>
#include <fmt/format.h>
#include "httplib.h"

std::string markdownToHtml(const std::string& markdown) {
    using namespace std;
    using namespace fmt;

    // Replace headers
    string html = "";
    auto start = markdown.find("#");
    while (start != string::npos) {
        auto end = markdown.find_first_of(" \n", start + 1);
        auto level = markdown.substr(start, end - start).size() - 1;
        html += string(level, '<') + "h";
        html += markdown.substr(start, end - start) + "</" + string(level, '/') + "h>";
        start = markdown.find("#", end);
    }

    // Process lists
    auto pos = markdown.find_first_of("-*+0", 0);
    while (pos != string::npos) {
        auto end = markdown.find_first_not_of(" \t\n", pos);
        auto item = markdown.substr(pos, end - pos);
        auto listType = markdown[pos];
        if (listType == '-' || listType == '*' || listType == '+') {
            html += "<ul>" + item + "</li>\n";
        } else {
            html += "<ol>" + item + "</li>\n";
        }

        pos = markdown.find_first_of("-*+0", end);
    }

    // Process links and images
    map<string, string> references;
    pos = markdown.find("[");
    while (pos != string::npos) {
        auto linkStart = pos;
        auto linkEnd = markdown.find_first_of("]", '\n', '\t', ' ', markdown, pos);
        auto text = markdown.substr(linkStart + 1, linkEnd - linkStart - 1);

        pos = markdown.find("(", linkEnd);
        auto urlStart = pos + 1;
        auto urlEnd = markdown.find_first_of(")", '\n', '\t', ' ', markdown, urlStart);
        auto url = markdown.substr(urlStart, urlEnd - urlStart);

        if (markdown[urlEnd - 1] == ']') {
            // inline link
            html += text + "<a href=\"" + url + "\">" + url + "</a>";
        } else {
            // reference link or image
            auto idStart = markdown.find(":", urlEnd);
            auto idEnd = markdown.find_first_not_of(" \t\n", idStart + 1);
            string id = markdown.substr(idStart + 1, idEnd - idStart - 1);
            references[id] = url;
            html += text + "<a href=\"" + url + "\">" + text + "</a>";
        }

        pos = markdown.find("[", pos);
    }

    // Replace references
    for (const auto& [id, url] : references) {
        html = replace(html, id, url);
    }

    return html;
}

// Example usage
int main() {
    string markdown = R"(
        # Header 1
        ## Header 2
        ### Header 3
        * Apple
        * Banana
        1. Orange
        2. Grapefruit
        [Link Text](http://example.com)
        [Another Link Text][id]
        [id]: http://example.com
        ![Alt Text](http://example.com)
        [Alt Text][id]
        [id]: http://example.com
    )";

    cout << markdownToHtml(markdown);
    return 0;
}
```

